import styled from 'styled-components';

export const HeaderProfileContainer = styled.div`
  width: 100%;
  height: auto;
  z-index: 10;
  display: flex;
  align-items: center;
  padding-top: 24px;
  padding-bottom: 80px;
`;

export const GridContainer = styled.div`
  height: 100%;
  width: 100%;
  margin: auto 24px;
`;

export const TitleContainer = styled.span`
  display: block;
  font-family: 'Gilroy-Bold';
  text-align: center;
  font-size: 24px;
  color: var(--primary-green);
`;

export const SubtitleContainer = styled.span`
  display: block;
  font-family: 'Gilroy-SemiBold';
  text-align: center;
  font-size: 14px;
  color: var(--base-gray-light);
`;

export const BronzeFlag = styled.img`
  width: 291px;
  height: auto;
  margin: auto;
`;

export const CurrentElo = styled.div`
  width: 98px;
  height: 42px;
  text-transform: uppercase;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 24px;
  border: 2px solid white;
  background: blue;
  color: white;
  position: absolute;
  top: -24px;
  left: 50%;
  transform: translateX(-50%);
`;

export const FlagContainer = styled.div`
  position: relative;
  height: 100%;
  width: 100%;
  max-width: 300px;
  margin: auto;
`;

export const FlagImg = styled.img`
  position: relative;
  height: 100%;
  width: 100%;
  max-width: 240px;
`;

export const Capacete2 = styled.img`
  width: 330px;
  height: auto;
  position: absolute;
  bottom: -70px;
  margin: auto;
  left: 50%;
  transform: translateX(-50%);
`;

export const Capacete1 = styled.img`
  width: 306px;
  height: auto;
  position: absolute;
  top: -201px;
  margin: auto;
  left: 50%;
  transform: translateX(-50%);
`;

export const TextFlagContainer = styled.div`
  position: absolute;
  top: 54%;
  left: 50%;
  transform: translate(-50%, -50%);
  text-align: center;
  color: white;
`;

export const Elo = styled.p`
  font-weight: bold;
  font-size: 16px;
  padding: 4px 0px;
  position: absolute;
  color: white;
  left: 29%;
  top: 56%;
  z-index: 10;
  &::after {
    content: '';
    height: 24px;
    width: 24px;
    display: block;
    background: url('https://imgur.com/8toqDzW.png');
    background-size: 24px;
    z-index: 11;
    position: absolute;
    cursor: pointer;
    right: -24px;
    top: 0;
  }
  @media only screen and (max-width: 600px) {
    font-size: 13px;
    left: 40%;
  }
`;

export const Queue = styled.p`
  font-weight: bold;
  font-size: 16px;
  padding: 4px 0px;
  position: absolute;
  color: white;
  left: 34%;
  top: 65%;
  z-index: 10;
  text-align: center;
  text-transform: uppercase;
  @media only screen and (max-width: 600px) {
    font-size: 13px;
    left: 43%;
  }
`;

export const Pdl = styled.p`
  font-weight: bold;
  font-size: 16px;
  padding: 4px 0px;
  position: absolute;
  color: white;
  left: 27%;
  top: 60.2%;
  z-index: 10;
  &::after {
    content: '';
    height: 24px;
    width: 24px;
    display: block;
    background: url('https://imgur.com/8toqDzW.png');
    background-size: 24px;
    z-index: 11;
    position: absolute;
    cursor: pointer;
    right: -24px;
    top: 0;
  }
  @media only screen and (max-width: 600px) {
    font-size: 13px;
    left: 39%;
  }
`;

export const BronzeElo = styled.p`
  font-weight: bold;
  font-size: 16px;
  position: relative;
  padding: 4px 0px;
  position: absolute;
  color: white;
  left: 25%;
  top: 57%;
  z-index: 10;
  &::after {
    content: '';
    height: 24px;
    width: 24px;
    display: block;
    background: url('https://imgur.com/8toqDzW.png');
    background-size: 24px;
    z-index: 11;
    position: absolute;
    cursor: pointer;
    right: -24px;
    top: 0;
  }
  @media only screen and (max-width: 600px) {
    font-size: 13px;
    left: 38%;
  }
`;

export const BronzeQueue = styled.p`
  font-weight: bold;
  font-size: 16px;
  padding: 4px 0px;
  position: absolute;
  color: white;
  left: 32%;
  top: 67%;
  z-index: 10;
  text-align: center;
  text-transform: uppercase;
  @media only screen and (max-width: 600px) {
    font-size: 13px;
    left: 43%;
  }
`;

export const BronzePdl = styled.p`
  font-weight: bold;
  font-size: 16px;
  padding: 4px 0px;
  position: absolute;
  color: white;
  left: 27%;
  top: 62%;
  z-index: 10;
  &::after {
    content: '';
    height: 24px;
    width: 24px;
    display: block;
    background: url('https://imgur.com/8toqDzW.png');
    background-size: 24px;
    z-index: 11;
    position: absolute;
    cursor: pointer;
    right: -24px;
    top: 0;
  }
  @media only screen and (max-width: 600px) {
    font-size: 13px;
    left: 39%;
  }
`;

export const CheckoutOrderBoxContainer = styled.div`
  width: 100%;
  background: white;
  height: 100%;
  border-radius: 24px;
`;

export const CheckoutHeader = styled.div`
  width: 100%;
  height: 52px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  background-color: #010c1c;
  border-top: 1px solid #6F787F;
  border-right: 1px solid #6F787F;
  border-left: 1px solid #6F787F;
`;

export const CheckoutBody = styled.div`
  width: 100%;
  height: 342px;
  background-color: white;
  border-top-left-radius: 16px;
  border-top-right-radius: 16px;
`;


export const SubTitleBox = styled.div`
  padding: 16px 24px 4px;
  display: flex;
  justify-content: space-between;
`;

export const OrderBox = styled.div`
  display: flex;
  padding: 0 24px 16px;
  margin: 0 0;
  border-bottom: 1px solid #3B3B3B;
  justify-content: space-between;
  background: white;
`;

export const Division = styled.div`
  font-weight: bold;
  font-size: 14px;
  text-transform: uppercase;
  color: black;
`;

export const SalePrice = styled.div`
  font-weight: bold;
  font-size: 14px;
  text-transform: uppercase;
  color: #3B3B3B;
  text-decoration: line-through;
`;

export const BoostOrder = styled.div`
  font-weight: bold;
  font-size: 12px;
  font-family:'Gilroy-Regular';
  text-transform: uppercase;
  margin: auto 0;
  color: #3B3B3B;
`;

export const CheckoutHeaderTitle = styled.div`
  font-size: 14px;
  padding-left: 16px;
  padding-top: 4px;
  font-family:'Gilroy-Regular';
  color: #F6B443;
`;

export const TotalPrice = styled.div`
  font-weight: bold;
  font-size: 16px;
  text-transform: uppercase;
  width: 100px;
  text-align: right;
  background: white;
  color: #4C7CDC;
`;

export const OrderAditionsBox = styled.div`
  padding: 24px 24px 16px;
  margin: 0 0;
  font-weight: normal;
  border-bottom: 1px solid #3B3B3B;
  background: white;
`;

export const OrdersAditionsTitle = styled.div`
  color: black;
`;

export const FreeChampionAdition = styled.div`
  color: #3B3B3B;
  font-family: 'Gilroy';
  justify-content: space-between;
  display: flex;
  margin: 16px 0 0;
`;

export const SpellsAdition = styled.div`
  color: #3B3B3B;
  justify-content: space-between;
  display: flex;
  margin: auto 0;
`;

export const OfflineModeAdition = styled.div`
  color: #3B3B3B;
  justify-content: space-between;
  display: flex;
  margin: auto 0;
`;

export const VPNAdition = styled.div`
  color: #3B3B3B;
  justify-content: space-between;
  display: flex;
  margin: auto 0;
`;

export const TotalAmountBox = styled.div`
  padding: 16px 24px 16px;
  margin: 0 0;
  font-weight: normal;
  border-bottom: 1px solid #3B3B3B;
  background: white;
`;

export const TotalTitle = styled.div`
  color: black;
`;

export const TotalValue = styled.div`
  color: #4C7CDC;
  font-size: 24px;
`;

export const ExpressOrderBox = styled.div`
  padding: 16px 24px 16px;
  margin: 0 0;
  font-weight: normal;
  border-bottom: 1px solid #3B3B3B;
`;

export const ExpressOrder = styled.div`
  display: flex;
  justify-content: space-between;
`;

export const ButtonOrderBox = styled.div`
  border-radius: 4px;
  text-align: center;
  width: 100%;
  padding: 24px 0;
  cursor: pointer;
  background: white;
`;

export const ButtonOrder = styled.div`
  margin: 0 24px;
  color: white;
  border-radius: 4px;
  text-align: center;
  background: #ED3833;
  height: 42px;
  display: flex;
  justify-content: center;
  align-items: center;
`;
