import React, { useState, useCallback, useEffect } from 'react';

import Grid from '@material-ui/core/Grid';
import styled from 'styled-components';
import Box from '@material-ui/core/Box';
import Paper from '@material-ui/core/Paper';

import Head from 'next/head';

const IndexPageContainer = styled.div`
  background: #212529;
  height: 100%;
`;

const HeaderContainer = styled.header`
	margin: auto;
	text-align: center;
	margin: 42px 0;
	padding: 0 16px;
`;

const ContentContainer = styled.div`
	margin: 0 280px;
	width: 100%;
	display: flex;
	justify-content: center;
`;

const Separator = styled.div`
	margin-bottom: 180px;
`;

const SeparatorLeft = styled.div`
	margin-left: 120px;
`;

const FlagBronze = styled.div`
	width: 280px;
	height: 268px;
	display: flex;
	background: #c25836;
	margin: auto;
	border-top-right-radius: 8px;
	border-top-left-radius: 8px;
	-webkit-box-shadow: -1px 2px 11px 0px rgba(0,0,0,0.75);
	-moz-box-shadow: -1px 2px 11px 0px rgba(0,0,0,0.75);
	box-shadow: -1px 2px 11px 0px rgba(0,0,0,0.75);
	position: relative;
	::after {
		content: "";
    position: absolute;
    width: 0;
    height: 0;
		bottom: -198px;
		left: 0px;
    box-sizing: border-box;

    border: 99px solid black;
    border-color: transparent transparent #c25836 #c25836;

    transform-origin: 0 0;
    transform: rotate(-45deg);

    box-shadow: -3px 3px 3px 0 rgba(0, 0, 0, 0.4);
	}
`;

const FlagGold = styled.div`
	width: 280px;
	height: 268px;
	display: flex;
	margin: auto;
	background: #e4b866;
	border-top-right-radius: 8px;
	border-top-left-radius: 8px;
	-webkit-box-shadow: -1px 2px 11px 0px rgba(0,0,0,0.75);
	-moz-box-shadow: -1px 2px 11px 0px rgba(0,0,0,0.75);
	box-shadow: -1px 2px 11px 0px rgba(0,0,0,0.75);
	position: relative;
	::after {
		content: "";
    position: absolute;
    width: 0;
    height: 0;
		bottom: -198px;
		left: 0px;
    box-sizing: border-box;

    border: 99px solid black;
    border-color: transparent transparent #e4b866 #e4b866;

    transform-origin: 0 0;
    transform: rotate(-45deg);

    box-shadow: -3px 3px 3px 0 rgba(0, 0, 0, 0.4);
	}
`;

const Title = styled.h1`
	color: white;
	font-size: 32px;
	text-transform: uppercase;
	font-family: 'Gilroy-Bold';
`;

const SubTitle = styled.h1`
	color: white;
	font-size: 16px;
	font-family: 'Gilroy-Light';
`;

const App = () => {
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  return (
    <IndexPageContainer>
      <Head>
        <title>Lol Boost</title>
        <meta charSet='utf-8' />
        <meta httpEquiv='X-UA-Compatible' content='IE=edge' />
        <meta name='viewport' content='width=device-width,initial-scale=1,minimum-scale=1,maximum-scale=1,user-scalable=no' />
        <meta name='description' content='Programa de seleção, capacitação e conexão de talentos às empresas do mercado digital.' />
        <meta name='keywords' content='Keywords' />
        <meta property='og:image' content='favicon-96x96.png'></meta>

        <link rel='manifest' href='/manifest.json' />
        <link href='favicon-72x72.png' rel='icon' type='image/png' sizes='72x72' />
        <link href='favicon-96x96.png' rel='icon' type='image/png' sizes='96x96' />
        <link rel='apple-touch-icon' href='/apple-icon.png'></link>
        <meta name='theme-color' content='#63DC3E' />
      </Head>
      <Grid
        container
        direction="row"
        justify="center"
        alignItems="center"
        >
					<HeaderContainer>
						<Title>Solo Boost </Title>
						<SubTitle>Guaranteed Division you desire, with the Booster playing in your account.</SubTitle>
					</HeaderContainer>
      </Grid>
			<Grid
        container
        direction="row"
        justify="center"
				alignItems="center"
        >
					<ContentContainer>
						<Grid item direction="row"
						justify="center"
						alignItems="center" xs={12} md={6}>
							<FlagBronze></FlagBronze>
						</Grid>
						<Box display={{ xs: 'block', md: 'none' }} style={ { height: '100%', width: '100%' } }>
							<Separator></Separator>
						</Box>
						<Grid item direction="row"
							justify="center"
							alignItems="center" xs={12} md={6}>
							<FlagGold></FlagGold>
						</Grid>
					</ContentContainer>
				</Grid>
    </IndexPageContainer>
  );
}

export default App;
