import styled from 'styled-components';

export const HeaderProfileContainer = styled.div`
  width: 100%;
  height: auto;
  position: relative;
  z-index: 10;
  display: flex;
  align-items: center;
  padding-bottom: 80px;
`;

export const GridContainer = styled.div`
  height: 100%;
  width: 100%;
  margin: auto 24px;
`;

export const TitleContainer = styled.span`
  display: block;
  font-weight: 700;
  text-align: center;
  font-size: 24px;
  color: var(--primary-green);
`;

export const VictoryCalculatorContainer = styled.div`
  width: 240px;
  text-transform: uppercase;
  color: white;
  font-size: 24px;
  text-align: center;
  margin: auto;
  @media only screen and (max-width: 768px) {
    margin: 32px auto;
  }
`;

export const VictoryCalculatorHeader = styled.div`
  height: 80px;
  background: #17387A;
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 24px;
  border: 2px solid white;
  z-index: 10;
  position: relative;
`;

export const VictoryCalculatorBody = styled.div`
  height: 120px;
  z-index: 2;
  position: relative;
  top: -34px;
  background: #3FA7E6;
  display: flex;
  justify-content: center;
  font-size: 42px;
  align-items: center;
  border-radius: 24px;
  border: 2px solid white;
`;

export const VictoryCalculatorSpan = styled.span`
  margin: 0 8px;
  padding-top: 24px;
`;

export const SubtitleContainer = styled.span`
  display: block;
  font-family: 'Roboto-SemiBold';
  text-align: center;
  font-size: 14px;
  color: var(--base-gray-light);
`;

export const QueueContainer = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
`;
export const QueueHeaderContainer = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
  margin-top: 24px;
  font-weight: 600;
`;

export const QueueHeader = styled.div`
  height: 24px;
  width: 202px;
  font-weight: 600;
  color: #68749D;
