import React, { useEffect } from 'react';

import Grid from '@material-ui/core/Grid';
import { withStyles } from '@material-ui/core/styles';
import Switch from '@material-ui/core/Switch';
import CircularProgress from '@material-ui/core/CircularProgress';
import Rating from '@material-ui/lab/Rating';
import "isomorphic-fetch";

import { 
    HeaderProfileContainer,
    GridContainer,
    FlagContainer,
    FlagImg,
    Capacete2,
    Capacete1,
    BronzeFlag,
    TextFlagContainer,
    Elo,
    Pdl,
    Queue,
    BronzeElo,
    BronzePdl,
    BronzeQueue,
    CheckoutOrderBoxContainer,
    CheckoutHeader,
    SubTitleBox,
    OrderBox,
    Division,
    SalePrice,
    BoostOrder,
    TotalPrice,
    OrderAditionsBox,
    FreeChampionAdition,
    OrdersAditionsTitle,
    SpellsAdition,
    OfflineModeAdition,
    VPNAdition,
    TotalAmountBox,
    TotalTitle,
    TotalValue,
    ExpressOrderBox,
    CheckoutHeaderTitle,
    ExpressOrder,
    ButtonOrderBox,
    ButtonOrder
  } from './calculator-styles';

import ironFlag from "assets/bandeira ouro 2 (1).png";
import bronzeflag from "assets/bandeira bronze 2 (1).png";

import SelectElo from "components/modals/SelectElo";

const PurpleSwitch = withStyles({
  switchBase: {
    color: '#797979',
    '&$checked': {
      color: '#4C7CDC',
    },
    '&$checked + $track': {
      backgroundColor: '#446fc4',
    },
  },
  checked: {},
  track: {},
})(Switch);

let checked = {};

const Calculator = () => {

  const [state, setState] = React.useState({
    checkedA: true,
    checkedB: true,
    checkedC: true,
    checkedD: true,
  });

  let elos = [];
  let totalPrice = 0;

  const [modalState, setModalState] = React.useState(false);
  const [modalStateFrom, setModalStateFrom] = React.useState(false);

  const [services, setServices] = React.useState([]);
  const [total, setTotal] = React.useState(0);
  const [displayedTotal, setDisplayedTotal] = React.useState(0);
  const [additionalCost, setAditionalCost] = React.useState(0);


  const handleChange = (event) => {
    setState({ ...state, [event.target.name]: event.target.checked });
  };

  const handleModalChange = () => {
    setModalState(!modalState);
  };
  const handleModalChangeFrom = () => {
    setModalStateFrom(!modalStateFrom);
  };

  useEffect(() => {    
    (async() => {
      const resServices = await fetch(`https://lit-headland-64162.herokuapp.com/service`);
      const servicesJson = await resServices.json();
      const resElos = await fetch(`https://lit-headland-64162.herokuapp.com/elo`);
      const elosJson = await resElos.json();
      setServices(servicesJson);
      servicesJson.forEach(service => {
        checked[`${service.name}`] = false;
      });
      elosJson.forEach(elo => {
        let temp = {};
        temp[`${elo.name}`] = elo.value;
        elos.push(temp);
      });
      console.log('response -> ', servicesJson);
      console.log('checked -> ', checked);
      console.log('elos -> ', elos);

      let url = 'bronze-4-para-ouro-4';
      let from = url.split("-para-")[0].replace("-", " ").toUpperCase();
      let to = url.split("-para-")[1].replace("-", " ").toUpperCase();
      let fromIndex = elos.findIndex(e => Object.keys(e)[0] == from );
      let toIndex = elos.findIndex(e => Object.keys(e)[0] == to );
      for(let i = fromIndex; i <= toIndex; i++){
        let eloPrice = Number.parseFloat(Object.values(elos[i])[0]);
        console.log("eloprice ", eloPrice);
        totalPrice += eloPrice;
      }
      console.log("teste");
      console.log('total ', totalPrice);
      setTotal(totalPrice);
      setDisplayedTotal(totalPrice);
    })();
  }, []);

  const handleCheckChange = (serviceName) => {
    let myService = services.find((service) => {
      return service.name == serviceName
    });
    let servicePercentage = Number.parseFloat(myService.value);
    let additionalCost2 = (servicePercentage * total) / 100;

    console.log('additionalCost? ', additionalCost2);

    if(!checked[serviceName]) {
      setAditionalCost(additionalCost2 + additionalCost);
      setDisplayedTotal(total + additionalCost);
    } else {
      setAditionalCost(additionalCost2 + additionalCost);
      setDisplayedTotal(total - additionalCost);
    }
    checked[serviceName] = !checked[serviceName];
  }

  return (
    <HeaderProfileContainer>
      <SelectElo handleClose={ handleModalChange } open={ modalState } desired={ true } disabledFrom={ 'bronze 4'}  />
      <SelectElo handleClose={ handleModalChangeFrom } open={ modalStateFrom } desired={ false } disabledFrom={ 'ferro 4'} />
      <GridContainer>
        <Grid container spacing={2}
          direction="row"
          justify="center"
          alignItems="center">
          <Grid item xs={12} s={12} md={4}>
            <FlagContainer>
              <BronzeElo onClick={ () => { setModalStateFrom(true) } } >
                BRONZE IV
              </BronzeElo>
              <BronzePdl>
                0 - 20 PDL
              </BronzePdl>
              <BronzeQueue>
                Solo
              </BronzeQueue>
              <FlagImg src={ bronzeflag } />
            </FlagContainer>
          </Grid>
          <Grid item xs={12} s={12} md={4}>
            <FlagContainer>
              <Elo onClick={ () => { setModalState(true) } } >
                OURO IV
              </Elo>
              <Pdl onClick={ () => {} } >
                0 - 20 PDL
              </Pdl>
              <Queue>
                Solo
              </Queue>
              <FlagImg src={ ironFlag } />
            </FlagContainer>
          </Grid>
          <Grid item xs={12} s={12} md={4}>
            <CheckoutOrderBoxContainer>
              <CheckoutHeader>
                <CheckoutHeaderTitle>
                  234 compraram
                </CheckoutHeaderTitle>
                <Rating name="read-only" value={5} readOnly  style={ { paddingLeft: '16px'} }/>
              </CheckoutHeader>
              <SubTitleBox>
                <Division>
                  SOLO DIVISIONS
                </Division>
                <SalePrice>
                  { total.toFixed(2).toString().replace(".", ",") } R$
                </SalePrice>
              </SubTitleBox>
              <OrderBox>
                <BoostOrder>Bronze 4 (0 PDL - 25 PDL) - Ouro 4</BoostOrder>
                <TotalPrice>{ total.toFixed(2).toString().replace(".", ",") } R$</TotalPrice>
              </OrderBox>
              <OrderAditionsBox>
                <OrdersAditionsTitle>PRODUTOS ADICIONAIS</OrdersAditionsTitle>
                { services.length > 0 ? services.map((service, index) => {
                  console.log(index)
                  return (
                  <FreeChampionAdition style={ index > 0 ? { marginTop: 0 } : {} }>
                    <span style={ { margin: 'auto 0', fontFamily:'Gilroy-Regular' } }>{ service.name }</span>
                    <PurpleSwitch checked={checked[service.name]} onChange={() => { handleCheckChange(service.name) }} name="checkedA"/>
                  </FreeChampionAdition>
                  )
                }) : <CircularProgress style={ { marginTop: '16px' } }/> }
              </OrderAditionsBox>
            </CheckoutOrderBoxContainer>
            <TotalAmountBox>
              <TotalTitle>TOTAL</TotalTitle>
              <TotalValue>{ displayedTotal.toFixed(2).toString().replace(".", ",") } R$</TotalValue>
            </TotalAmountBox>
            <ButtonOrderBox>
              <ButtonOrder>Comprar Boost</ButtonOrder>
            </ButtonOrderBox>
          </Grid>
        </Grid>
      </GridContainer>

    </HeaderProfileContainer>
  );
}

export default Calculator;
