import styled from 'styled-components';

export const HeaderProfileContainer = styled.div`
  width: 100%;
  height: auto;
  position: relative;
  z-index: 10;
  display: flex;
  align-items: center;
  padding-bottom: 80px;
`;

export const GridContainer = styled.div`
  height: 100%;
  width: 100%;
  margin: auto 24px;
`;

export const TitleContainer = styled.span`
  display: block;
  font-family: 'Gilroy-Bold';
  text-align: center;
  font-size: 24px;
  color: var(--primary-green);
`;

export const VictoryCalculatorContainer = styled.div`
  width: 240px;
  text-transform: uppercase;
  color: white;
  font-size: 24px;
  text-align: center;
`;

export const VictoryCalculatorHeader = styled.div`
  height: 80px;
  background: #17387A;
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 24px;
  border: 2px solid white;
  z-index: 4;
`;

export const VictoryCalculatorBody = styled.div`
  height: 100px;
  z-index: 3;
  position: relative;
  top: -24px;
  background: #3FA7E6;
  display: flex;
  justify-content: center;
  font-size: 42px;
  align-items: center;
  border-radius: 24px;
  border: 2px solid white;
`;

export const VictoryCalculatorSpan = styled.span`
  margin: 0 8px;
`;

export const SubtitleContainer = styled.span`
  display: block;
  font-family: 'Gilroy-SemiBold';
  text-align: center;
  font-size: 14px;
  color: var(--base-gray-light);
`;

export const QueueContainer = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
`;

export const QueueBox = styled.div`
  height: 52px;
  width: 202px;
  position: relative;
  right: 0px;
  background: #0000ee;
  color: white;
  padding: 8 24px;
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 28px;
  border-bottom-right-radius: 42px;
  border-bottom-left-radius: 42px;
  @media only screen and (max-width: 768px) {
    position: inherit;
    right: 0px;
  }
  &::after {
    content: '';
    display: block;
    height: 32px;
    width: 32px;
    background: transparent;
    position: absolute;
    background-image: url(${require('../assets/arrow2.png')});
    background-size: 32px;
    cursor: pointer;
    transform: rotate(180deg);
    right: 24px;
  }
`;

export const SwitchesLeftText = styled.div`
  color: #112E4D;
  margin-left: 24px;
  position: relative;
  text-transform: uppercase;
  &::before{
    content: '';
    position: absolute;
    display: block;
    left: -12px;
    height: 54px;
    width: 4px;
    background-color: #399CDA;
  }
`;

export const SwitchesLeftText2 = styled.div`
  margin-top: 32px;
  color: #112E4D;
  margin-left: 24px;
  position: relative;
  text-transform: uppercase;
  &::before{
    content: '';
    position: absolute;
    display: block;
    left: -12px;
    height: 54px;
    width: 4px;
    background-color: #E7AA3E;
  }
`;

export const SwitchesLeftBoldText = styled.div`
  font-family:'Gilroy-Bold' !important;;
  color: #112E4D;
  font-size: 24px;
`;

export const SwitchesLeftLightText = styled.div`
  font-family:'Gilroy-Light' !important;
  color: #112E4D;
  font-size: 24px;
`;

export const BronzeFlag = styled.img`
  width: 291px;
  height: auto;
  margin: auto;
`;

export const CurrentElo = styled.div`
  width: 142px;
  height: 42px;
  text-transform: uppercase;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 24px;
  border: 2px solid white;
  background: blue;
  color: white;
  position: absolute;
  font-size: 16px;
  top: -52px;
  left: 50%;
  transform: translateX(-50%);
  @media only screen and (max-width: 768px) {
    left: 50%;
  }
`;

export const DesiredElo = styled.div`
  width: 152px;
  height: 42px;
  text-transform: uppercase;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 24px;
  border: 2px solid white;
  background: #0000dd;
  color: white;
  position: absolute;
  font-size: 16px;
  top: -68px;
  left: 50%;
  transform: translateX(-50%);
  @media only screen and (max-width: 768px) {
    left: 50%;
  }
`;

export const FlagContainer = styled.div`
  position: relative;
  height: 100%;
  width: 100%;
  display: flex;
  justify-content: center;
  max-width: 300px;
  margin: auto;
`;

export const FlagImg = styled.img`
  position: relative;
  height: 100%;
  width: 100%;
  max-width: 240px;
`;

export const Capacete2 = styled.img`
  width: 330px;
  height: auto;
  position: absolute;
  bottom: -70px;
  margin: auto;
  left: 50%;
  transform: translateX(-50%);
`;

export const Capacete1 = styled.img`
  width: 306px;
  height: auto;
  position: absolute;
  top: -201px;
  margin: auto;
  left: 50%;
  transform: translateX(-50%);
`;

export const TextFlagContainer = styled.div`
  position: absolute;
  top: 54%;
  left: 50%;
  transform: translate(-50%, -50%);
  text-align: center;
  color: white;
`;

export const Elo = styled.p`
  font-weight: bold;
  font-size: 16px;
  padding: 4px 0px;
  position: absolute;
  color: white;
  left: inherit;
  top: 56%;
  z-index: 10;
  &::after {
    content: '';
    height: 24px;
    width: 24px;
    display: block;
    background: url('https://imgur.com/8toqDzW.png');
    background-size: 24px;
    z-index: 11;
    position: absolute;
    cursor: pointer;
    right: -24px;
    top: 0;
  }
  @media only screen and (max-width: 768px) {
    font-size: 13px;
    left: inherit;
  }
`;

export const Queue = styled.p`
  font-weight: bold;
  font-size: 16px;
  padding: 4px 0px;
  position: absolute;
  color: white;
  left: inherit;
  top: 65%;
  z-index: 10;
  text-align: center;
  text-transform: uppercase;
  @media only screen and (max-width: 768px) {
    font-size: 13px;
