        <SwitchDivision2 style={ flexQueue ?  { color: 'white', background: 'linear-gradient(90deg, rgba(248,211,73,1) 0%, rgba(245,173,66,1) 49%, rgba(241,128,58,1) 100%)'} : {} } >
