import styled from 'styled-components';

export const HeaderProfileContainer = styled.div`
  background: white;
  min-height: 200px;
  position: fixed;
  width: 100%;
  z-index: 10;
  display: flex;
  align-items: center;
`;

export const GridContainer = styled.div`
  height: 100%;
  width: 100%;
  margin: auto;
`;

export const TitleContainer = styled.span`
  display: block;
  font-family: 'Gilroy-Bold';
  text-align: center;
  font-size: 24px;
  color: var(--primary-green);
`;

export const SubtitleContainer = styled.span`
  display: block;
  font-family: 'Gilroy-SemiBold';
  text-align: center;
  font-size: 14px;
  color: var(--base-gray-light);
`;

export const BronzeFlag = styled.img`
  width: 291px;
  height: auto;
  margin: auto;
`;

export const FlagContainer = styled.div`
  position: relative;
  height: 100%;
  width: 291px;
  margin: auto;
  display: flex;
`;

export const Capacete2 = styled.img`
  width: 330px;
  height: auto;
  position: absolute;
  bottom: -70px;
  margin: auto;
  left: 50%;
  transform: translateX(-50%);
`;

export const Capacete1 = styled.img`
  width: 306px;
  height: auto;
  position: absolute;
  top: -201px;
  margin: auto;
  left: 50%;
  transform: translateX(-50%);
`;

export const TextFlagContainer = styled.div`
  position: absolute;
  top: 54%;
  left: 50%;
  transform: translate(-50%, -50%);
  text-align: center;
  color: white;
`;

export const Elo = styled.p`
  font-weight: bold;
  font-size: 16px;
  padding: 4px 0px;
`;

export const Queue = styled.p`
  font-size: 16px;
  padding: 4px 0px;
`;

export const Pdl = styled.p`
  font-weight: bold;
  font-size: 14px;
  font-weight: bold;
  text-transform: uppercase;
  padding: 4px 0px;
`;

export const CheckoutOrderBoxContainer = styled.div`
  width: 100%:
  height: 100%:
  border-radius: 24px;
`;

export const CheckoutHeader = styled.div`
  width: 100%;
  height: 42px;
  background-color: #010c1c;
  border-top: 1px solid #6F787F;
  border-right: 1px solid #6F787F;
  border-left: 1px solid #6F787F;
`;

export const CheckoutBody = styled.div`
  width: 100%;
  height: 342px;
  background-color: white;
  border-top-left-radius: 16px;
  border-top-right-radius: 16px;
`;


export const SubTitleBox = styled.div`
  padding: 16px 24px;
  display: flex;
  justify-content: space-between;
`;

export const OrderBox = styled.div`
  display: flex;
  padding: 0 24px 24px;
  margin: 0 24px 24px;
  border-bottom: 1px solid red;
  justify-content: space-between;
`;

export const Division = styled.div`
  font-weight: bold;
  font-size: 14px;
  text-transform: uppercase;
  color: black;
`;

export const SalePrice = styled.div`
  font-weight: bold;
  font-size: 14px;
  text-transform: uppercase;
  color: #B0B0B0;
  text-decoration: line-through;
`;

export const BoostOrder = styled.div`
  font-weight: bold;
  font-size: 14px;
  text-transform: uppercase;
  color: #B0B0B0;
`;

export const TotalPrice = styled.div`
  font-weight: bold;
  font-size: 16px;
  text-transform: uppercase;
  color: #4C7CDC;
  text-decoration: line-through;
`;
