import React, { useEffect } from 'react';

import Grid from '@material-ui/core/Grid';
import { withStyles } from '@material-ui/core/styles';
import Switch from '@material-ui/core/Switch';
import "isomorphic-fetch";

import { 
    HeaderProfileContainer,
    GridContainer,
    FlagContainer,
    FlagImg,
    Capacete2,
    Capacete1,
    BronzeFlag,
    TextFlagContainer,
    Elo,
    Pdl,
    Queue,
    BronzeElo,
    BronzePdl,
    BronzeQueue,
    CheckoutOrderBoxContainer,
    CheckoutHeader,
    SubTitleBox,
    OrderBox,
    Division,
    SalePrice,
    BoostOrder,
    TotalPrice,
    OrderAditionsBox,
    FreeChampionAdition,
    OrdersAditionsTitle,
    SpellsAdition,
    OfflineModeAdition,
    VPNAdition,
    TotalAmountBox,
    TotalTitle,
    TotalValue,
    ExpressOrderBox,
    ExpressOrder,
    ButtonOrderBox,
    ButtonOrder
  } from './calculator-styles';

import ironFlag from "assets/bandeira ouro 2 (1).png";
import bronzeflag from "assets/bandeira bronze 2 (1).png";

import SelectElo from "components/modals/SelectElo";

const PurpleSwitch = withStyles({
  switchBase: {
    color: '#797979',
    '&$checked': {
      color: '#4C7CDC',
    },
    '&$checked + $track': {
      backgroundColor: '#446fc4',
    },
  },
  checked: {},
  track: {},
})(Switch);

const Calculator = () => {

  const [state, setState] = React.useState({
    checkedA: true,
    checkedB: true,
    checkedC: true,
    checkedD: true,
  });

  const checked = {};

  const [modalState, setModalState] = React.useState(false);
  const [modalStateFrom, setModalStateFrom] = React.useState(false);

  const [services, setServices] = React.useState([]);


  const handleChange = (event) => {
    setState({ ...state, [event.target.name]: event.target.checked });
  };

  const handleModalChange = () => {
    setModalState(!modalState);
  };
  const handleModalChangeFrom = () => {
    setModalStateFrom(!modalStateFrom);
  };

  useEffect(() => {    
    (async() => {
      const resServices = await fetch(`https://lit-headland-64162.herokuapp.com/service`);
      const servicesJson = await resServices.json();
      setServices(servicesJson);
      Object.keys(servicesJson).map(key => {
        checked[`${key}`] = false;
      })
      console.log('response -> ', Object.keys(servicesJson));
      console.log('checked -> ', checked);
    })();
  }, []);

  return (
    <HeaderProfileContainer>
      <SelectElo handleClose={ handleModalChange } open={ modalState } desired={ true } disabledFrom={ 'bronze 4'}  />
      <SelectElo handleClose={ handleModalChangeFrom } open={ modalStateFrom } desired={ false } disabledFrom={ 'ferro 4'} />
      <GridContainer>
        <Grid container spacing={2}
          direction="row"
          justify="center"
          alignItems="center">
          <Grid item xs={12} s={12} md={4}>
            <FlagContainer>
              <BronzeElo onClick={ () => { setModalStateFrom(true) } } >
                BRONZE IV
              </BronzeElo>
              <BronzePdl>
                0 - 20 PDL
              </BronzePdl>
              <BronzeQueue>
                Solo
              </BronzeQueue>
              <FlagImg src={ bronzeflag } />
            </FlagContainer>
          </Grid>
          <Grid item xs={12} s={12} md={4}>
            <FlagContainer>
              <Elo onClick={ () => { setModalState(true) } } >
                OURO IV
              </Elo>
              <Pdl onClick={ () => {} } >
                0 - 20 PDL
              </Pdl>
              <Queue>
                Solo
              </Queue>
              <FlagImg src={ ironFlag } />
            </FlagContainer>
          </Grid>
          <Grid item xs={12} s={12} md={4}>
            <CheckoutOrderBoxContainer>
              <CheckoutHeader>

              </CheckoutHeader>
              <SubTitleBox>
                <Division>
                  SOLO DIVISIONS
                </Division>
                <SalePrice>
                  1,186.53$
                </SalePrice>
              </SubTitleBox>
              <OrderBox>
                <BoostOrder>Bronze 4 (0 LP - 25 LP) - Ouro 4</BoostOrder>
                <TotalPrice>889.90$</TotalPrice>
              </OrderBox>
              <OrderAditionsBox>
                <OrdersAditionsTitle>PRODUTOS ADICIONAIS</OrdersAditionsTitle>
                { services.map(service => {
                  <FreeChampionAdition>
                    <span style={ { margin: 'auto 0', fontFamily:'Gilroy-Regular' } }>{ service.name }</span>
                    <PurpleSwitch checked={state.checked[service.name]} onChange={handleChange(service.name)} name="checkedA"/>
                  </FreeChampionAdition>
                })}
              </OrderAditionsBox>
            </CheckoutOrderBoxContainer>
            <TotalAmountBox>
              <TotalTitle>TOTAL</TotalTitle>
              <TotalValue>889.90 $</TotalValue>
            </TotalAmountBox>
            <ExpressOrderBox>
              <ExpressOrder>
                <span style={ { margin: 'auto 0' } } >Express Order <span style={ { color: '#3B3B3B', margin:'auto 0' } } >(+25%)</span></span>
                <PurpleSwitch checked={state.checkedE} onChange={handleChange} name="checkedE"/>
              </ExpressOrder>
            </ExpressOrderBox>
            <ButtonOrderBox>
              <ButtonOrder>Purchase Boost</ButtonOrder>
            </ButtonOrderBox>
          </Grid>
        </Grid>
      </GridContainer>

    </HeaderProfileContainer>
  );
}

export default Calculator;
